import react from "react";
import Form from "../Components/form.js";
const qrcode = require('qrcode');
import {
  Button,
  Grid,
  Link,
  Snackbar,
  Typography,
  CircularProgress,
} from "@mui/material";
import Alert from "@mui/material/Alert";

const containerStyle = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  height: "100vh",
};

class Auth extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showForm: false,
      selectedForm: undefined,
      open: false,
      message: "",
      severity: "success",
      loading: false,
    };
  }

  closeForm = () => {
    this.setState({ showForm: false });
  };

  toggleForm = () => {
    const newForm = this.state.selectedForm === "login" ? "register" : "login";
    this.setState({ selectedForm: newForm });
  };

  login = (data) => {
    console.log(data);
    this.setState({ loading: true }); // Set loading state

    fetch(this.props.server_url + "/api/auth/login", {
      method: "POST",
      mode: "cors",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.msg === "Logged in") {
          this.props.changeScreen("lobby");
          this.handleSnackbarOpen("Logged in successfully!", "success");
        } else {
          this.handleSnackbarOpen(data.msg, "error");
        }
      })
      .catch(() => {
        this.handleSnackbarOpen("Network error!", "error");
      })
      .finally(() => {
        this.setState({ loading: false }); // Reset loading state
      });
  };

  register = (data) => {
    console.log(data);
    this.setState({ loading: true }); // Set loading state

    fetch(this.props.server_url + "/api/auth/signup", {
      method: "POST",
      mode: "cors",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((res) => res.clone().json())
      .then((data) => {
        this.handleSnackbarOpen("Registered successfully!", "success");
          qrcode.toDataURL(data.otpauth_url, function(err,data){
              console.log(data);
              document.getElementById("forQRcode").innerHTML = 'Use this QR code for 2FA <img src="' + data + '" alt="QR code"/>';
          })      })
      .catch(() => {
        this.handleSnackbarOpen("Network error!", "error");
      })
      .finally(() => {
        this.setState({ loading: false }); // Reset loading state
      });
  };

  handleSnackbarOpen = (message, severity) => {
    this.setState({
      open: true,
      message,
      severity,
    });
  };

  handleSnackbarClose = () => {
    this.setState({
      open: false,
      message: "",
    });
  };
    register = (data) => {
        // DONE: write codes to register
        console.log(data);
        // let r = JSON.stringify(data);
        fetch(this.props.server_url+"/api/auth/signup", {
            method: "POST",
            mode: 'cors',
            credentials: "include",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(data) // body data type must match "Content-Type" header above.
        }).then((res) => {
            res.clone().json().then((data) => {
                qrcode.toDataURL(data.otpauth_url, function(err,data){
                    console.log(data);
                    document.getElementById("forQRcode").innerHTML = 'Use this QR code for 2FA <img src="' + data + '" alt="QR code"/>';
                })
            }).catch(()=>{
                res.text().then((textData) => {
                alert(textData);
                })
            });
        });
    };

  render() {
    const { showForm, selectedForm, open, message, severity, loading } =
      this.state;

    let display = null;
    if (showForm) {
      let fields = [];
      if (selectedForm === "login") {
        fields = ["username", "password","OneTimePassword"];
        display = (
          <Form
            fields={fields}
            close={this.closeForm}
            type="login"
            submit={this.login}
            key={selectedForm}
          />
        );
      } else if (selectedForm === "register") {
        fields = ["username", "password", "name"];
        display = (
            <div>
          <Form
            fields={fields}
            close={this.closeForm}
            type="register"
            submit={this.register}
            key={selectedForm}
          />
          <div id="forQRcode"/>
        </div>
      );
      }
    } else {
      display = (
        <div>
          <Button
            onClick={() =>
              this.setState({ showForm: true, selectedForm: "login" })
            }
            variant="contained"
            size="large"
            sx={{ marginRight: 2 }}
            disabled={loading} // Disable button when loading
          >
            {loading ? <CircularProgress size={24} /> : "Login"}
          </Button>
          <Button
            onClick={() =>
              this.setState({ showForm: true, selectedForm: "register" })
            }
            variant="outlined"
            size="large"
            disabled={loading} // Disable button when loading
          >
            {loading ? <CircularProgress size={24} /> : "Register"}
          </Button>
        </div>
      );
    }

    return (
      <div style={containerStyle}>
        <div className="welcome">
          {open && (
            <Snackbar
              open={open}
              autoHideDuration={6000}
              onClose={this.handleSnackbarClose}
              anchorOrigin={{ vertical: "top", horizontal: "center" }}
            >
              <Alert
                onClose={this.handleSnackbarClose}
                severity={severity}
                sx={{ width: "100%" }}
              >
                {message}
              </Alert>
            </Snackbar>
          )}
          <Typography
            sx={{ mb: 20 }}
            // anchorOrigin={{ vertical: "top" }}
            variant="h4"
            component="h1"
            align="center"
            gutterBottom
          >
            Messenger
          </Typography>
          {display}
          {showForm && (
            <Grid container mt={2}>
              <Grid item xs>
                <Link
                  href="#"
                  variant="body2"
                  onClick={this.toggleForm}
                  sx={{ textDecoration: "underline", cursor: "pointer" }}
                >
                  {selectedForm === "login"
                    ? "Don't have an account? Sign Up"
                    : "Already have an account? Sign In"}
                </Link>
              </Grid>
            </Grid>
          )}
        </div>
      </div>
    );
  }
}

export default Auth;
